import Search from './Search';
import React, { useEffect, useState } from 'react';
import axios from 'axios'
import { Link } from 'react-router-dom';

const Header = () => {

    const [value,setValue]=useState('');
const [render,setRender]=useState(false);
const [response,setResponse]=useState(null);
const [city,setCity]=useState('');
    const handler=(cond)=>{
        setRender(cond)
       
    }
   
 useEffect(()=>{
    const getWeatherByCityName=async ()=>{
        try{
            console.log(value)
        const a=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=4503811c206df3e95177b13dae8d0921`)
        const b=a.data;
        setResponse(b);
        setCity(b.name);
        console.log(response)
        }catch(err){
            alert('something went wrong');
          
        }
      }
      getWeatherByCityName()
    },[city])
      
   
    return (
        <div>
            <input className="search"
                value={value}
                onChange={(e)=>setValue(e.target.value)} 
                style={{width:"400px",height:"35px"}} 
                type="text" placeholder="Search..."/>&nbsp;&nbsp;
               <Link to={{pathname:'/search',aboutprops:{
                   value:{response}
                }
               }}><button className="btn btn-primary">Search</button></Link>
              {response?<Search value={value} data={response}/>:null} 
               
        </div>
    );
};

export default Header;
