import React from 'react';
import axios from 'axios'
import Home from './components/Home';

const App = () => {
    let currentLocation='';
    let weatherData='';
    const geolocation=()=>{
        return new Promise(function(resolve,reject){
        navigator.geolocation.getCurrentPosition(resolve,reject);
        });
    }

    const getCoords=async ()=>{
        const coords=await geolocation();
        const {latitude:lat,longitude:lng}=coords.coords
        const response=await axios.get(`https://geocode.xyz/${lat},${lng}?geoit=json`);
        currentLocation=response.data.city;
        weatherData=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${currentLocation}&appid=4503811c206df3e95177b13dae8d0921`);

    }
    return (

        <div>
            {getCoords()}
           <Home weatherData={weatherData}/>
        </div>
    );
};

export default App;
