import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Home1 from './Home1';

const Search = (props) => {

    let [response,setResponse]=useState(null);
    const [city,setCity]=useState('');


    useEffect(()=>{
        const getWeatherByCityName=async ()=>{
            props.toggler(false)
            const a=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${props.value}&appid=4503811c206df3e95177b13dae8d0921`)
            setResponse(a);
            setCity(a.name)
          }
          getWeatherByCityName()
    },[city])
    
   
    return (
        <div>
        <Home1 data={response}/>
        </div>
    );
};

export default Search;
