import React, { useState } from 'react';
import axios from 'axios';
import Home1 from './Home1';

const Search = (props) => {
    props.toggler(false)
    const [response,setResponse]=useState('')
   
    const getWeatherByCityName=async ()=>{
    const a=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${props.value}&appid=4503811c206df3e95177b13dae8d0921`)
    setResponse(a);
    }
    getWeatherByCityName();
    return (
        <div>
            <Home1 data={response} />   
        </div>
    );
};

export default Search;
