import React, { useState } from 'react';
import Search from './Search';


const Home1 = ({data}) => {
    const [value,setValue]=useState('');
    const [render,setRender]=useState(false);
    const background=`${data?.weather[0]?.main}.jpg`;
    const icon=`${data?.weather[0]?.main}.png`
    var today = new Date();
    const day=['Sun','Mon','Tue','Wed','Thu','Fri','Sat']
    
    console.log(data)

   
    return (
        <div className="container-sm">            
            <div  className='background' style={{backgroundImage:`linear-gradient(rgba(0,0,0,0.5),rgba(0,0,0,0.5)),url(img/${background})`}}>
                <div className="container-fluid text-center" style={{width:"40%"}}>
                    {/* For Search */}
                
                <input className="search"
                value={value}
                onChange={(e)=>setValue(e.target.value)} 
                style={{width:"400px",height:"35px"}} 
                type="text" placeholder="Search..."/>&nbsp;&nbsp;
                <button onClick={()=>setRender(true)} className="btn btn-primary">Search</button>
                {render?<Search value={value}/>:null}
                {/*  */}
                    <div className="card card-background" >
                        <div className="card-body">
                            <div className="content">
                                <img className="icon" src={`img/${icon}`}/>
                                <span className="temp">{Math.round(data?.main.temp-273.15)}&#8451;</span><br/><br/>
                                <i class="fas fa-map-marker-alt"></i>&nbsp;&nbsp;
                                <h3 style={{display:"inline"}}>{data?.name}</h3>
                                <h3>{day[today.getDay()]}, {data?.weather[0].description}</h3>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="container-fluid text-center" style={{width:"40%"}}>
                    <div className="card card-background2" >
                        <div className="card-body">
                            <div className="content">
                            <table  style={{width:"100%"}}>
                                <tr>
                                <i className="fas fa-humidity" style={{color:"blue"}}></i>
                                    <th>Humidity</th>
                                    <td>{data?.main.humidity} %</td>
                                </tr>
                                <tr>
                                <i className="far fa-eye" style={{color:"rgb(211, 211, 0)"}}></i>
                                    <th>Visibility</th>
                                    <td>{data?.visibility} meter</td>
                                </tr>
                                <tr>
                                <i class="fas fa-wind" style={{color:"grey"}}></i>
                                   <th>Wind</th>
                                    <td>{data?.wind.speed} m/sec</td>
                                </tr>
                                </table>
                            </div>
                        </div>
                    </div>
                    
                </div>
            </div>
        </div>
       
        
    );
};

export default Home1;
