import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Home1 from './Home1';

const Search = (props) => {
    const [response,setResponse]=useState(null);
    const [city,setCity]=useState('');
    const [value,setValue]=useState('');
    const [render,setRender]=useState(false);

    useEffect(()=>{
        getWeatherByCityName();
       
    },[city])
    
    const getWeatherByCityName=async ()=>{
        try{
        const a=await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${props.value}&appid=4503811c206df3e95177b13dae8d0921`)
        const b=a.data;
        console.log(b)
        setResponse(b);
        setCity(b.name)
        }catch(err){
            document.querySelector('.loader').classList.add('hidden')
            alert('something went wrong');
           
        }
      }
   
    return (
        <div>
            <input className="search"
                value={value}
                onChange={(e)=>setValue(e.target.value)} 
                style={{width:"400px",height:"35px"}} 
                type="text" placeholder="Search..."/>&nbsp;&nbsp;
                <button onClick={()=>setRender(true)} className="btn btn-primary">Search</button>
        </div>
    );
};

export default Search;
